import java.util.Random;
import java.util.Scanner;

public class Game {
    public void start() {
        Scanner scanner = new Scanner(System.in);
        Random random = new Random();
        int numberToGuess = random.nextInt(100) + 1;
        int attempts = 0;
        int guess = -1;

        System.out.println("Willkommen zum Zahlenratespiel!");
        System.out.println("Ich habe mir eine Zahl zwischen 1 und 100 ausgedacht.");
        System.out.println("Versuche sie zu erraten. Gib 0 ein, um das Spiel zu beenden.");

        while (guess != numberToGuess && guess != 0) {
            System.out.print("Dein Tipp: ");
            try {
                guess = scanner.nextInt();
            } catch (Exception e) {
                System.out.println("Bitte gib eine gültige Zahl ein.");
                scanner.nextLine(); // Eingabepuffer leeren
                continue;
            }
            if (guess == 0) {
                System.out.println("Spiel beendet. Die gesuchte Zahl war: " + numberToGuess);
                break;
            }
            attempts++;
            if (guess < numberToGuess) {
                System.out.println("Zu niedrig!");
            } else if (guess > numberToGuess) {
                System.out.println("Zu hoch!");
            } else {
                System.out.println("Glückwunsch! Du hast die Zahl " + numberToGuess + " nach " + attempts + " Versuchen erraten.");
            }
        }
        scanner.close();
    }
}

import java.util.Random;
import java.util.Scanner;

public class JumpAndRun {
    public void start() {
        final int fieldLength = 20;
        char[] field = new char[fieldLength];
        for (int i = 0; i < fieldLength; i++) field[i] = '_';
        Random random = new Random();
        // Hindernisse zufällig platzieren (außer Start und Ziel)
        for (int i = 2; i < fieldLength - 1; i++) {
            if (random.nextInt(5) == 0) field[i] = 'X';
        }
        int playerPos = 0;
        Scanner scanner = new Scanner(System.in);
        System.out.println("Jump & Run - Erreiche das Ziel ohne Hindernisse zu treffen!");
        System.out.println("Gib 'springen' ein, um ein Hindernis zu überspringen, oder 'laufen', um ein Feld weiterzugehen.");
        while (playerPos < fieldLength - 1) {
            // Spielfeld anzeigen
            for (int i = 0; i < fieldLength; i++) {
                if (i == playerPos) System.out.print('P');
                else System.out.print(field[i]);
            }
            System.out.println();
            System.out.print("Aktion (springen/laufen): ");
            String action = scanner.nextLine().trim().toLowerCase();
            if (action.equals("springen")) {
                if (playerPos + 2 < fieldLength) playerPos += 2;
                else playerPos = fieldLength - 1;
            } else if (action.equals("laufen")) {
                playerPos++;
            } else {
                System.out.println("Ungültige Eingabe. Bitte 'springen' oder 'laufen' eingeben.");
                continue;
            }
            // Überprüfung auf Hindernis
            if (field[playerPos] == 'X') {
                System.out.println("Oh nein! Du bist gegen ein Hindernis gelaufen. Spiel verloren.");
                scanner.close();
                return;
            }
        }
        System.out.println("Herzlichen Glückwunsch! Du hast das Ziel erreicht.");
        scanner.close();
    }
}
